class toggleRequest{constructor(e){this.toggler=e,this.toggleRequest()}toggleRequest(){$(this.toggler).click((function(e){e.preventDefault();let t=$(this),n=t.children().first();$.ajax({type:"GET",url:$(n).attr("href")}).done((function(e){console.log("done");let s=$(n).attr("href").split("/"),o=s[s.length-1];e.data.code?($(t).html(`<a\n                href="/friends/remove/${o}"\n                class="btn btn-sm btn-block btn-danger"\n              >\n                <i class="ace-icon fa fa-remove bigger-120"></i>\n                <span class="bigger-110">Remove from friend list</span>\n              </a>`),new Noty({theme:"relax",text:"Added to friend list",type:"success",layout:"topRight",timeout:1500}).show()):($(t).html(`<a\n                href="/friends/add/${o}"\n                class="btn btn-sm btn-block btn-success"\n              >\n                <i class="ace-icon fa fa-plus-circle bigger-120"></i>\n                <span class="bigger-110">Add as a friend</span>\n              </a>`),new Noty({theme:"relax",text:"Removed from friend list",type:"success",layout:"topRight",timeout:1500}).show())})).fail((function(e){console.log("Error in toggle request ajax request",e)}))}))}}let convertProfileToAjax=function(){new toggleRequest($("#request-wrapper"))};convertProfileToAjax();